import React, { useState } from 'react'
//import logo from './logo.svg';
import './App.css';


const App = () => {
  const [persons, setPersons] = useState([
    {
      name: 'Arto Hellas',
      id: 1
    }
  ])
  const [newName, setNewName] = useState('')
  const add = (event) => {
    event.preventDefault()
    const Object = {
      name: newName,
      id: persons.length + 1
    }
    //console.log(Objext.content)
    setPersons(persons.concat(Object))
    setNewName('')
  }
  const handleNameChange = (event) => {
    //console.log(event.target.value)
    setNewName(event.target.value)
  }
  persons.forEach(props =>{
    if(props.name === newName) window.alert(`${newName} is already added to phonebook`)
  }
    )
 /*let isinclude = persons.includes(newName)
 console.log(isinclude)
 if (isinclude === true ) window.alert(`${newName} is already added to phonebook`)*/

  return (
    <div>
      <h2>Phonebook</h2>

      <form onSubmit={add}>
        <div>
          name:
          <input value={newName}
            onChange={handleNameChange}
          />

        </div>


        <div>
          <button type="submit">add</button>
        </div>
      </form>
      <h2>Numbers</h2>

      {persons && persons.map(note =>
        <li key={note.id}>   {note.name}</li>
      )}

    </div>
  )
}

export default App
