import React, { useState } from 'react'
//import logo from './logo.svg';
import './App.css';


const App = () => {
  const [persons, setPersons] = useState([
    { name: 'Arto Hellas', id: 1, phone: '1561234567' },
    { name: 'Ada Lovelace', id: 2, phone: '39-44-5323523' },
    { name: 'Dan Abramov', id: 3, phone: '12-43-234345' },
    { name: 'Mary Poppendieck', id: 4, phone: '39-23-6423122' }
  ])
  const [newName, setNewName] = useState('')
  const [newPhone, setNewPhone] = useState('')
  //const [filterName, setFNewilterName] = useState('')
  const add = (event) => {
    event.preventDefault()
    const Object = {
      name: newName,
      id: persons.length + 1,
      phone: newPhone
    }
    //console.log(Objext.content)
    persons.forEach(props => {
      //console.log(props.name)
      if (props.name === newName) {
        window.alert(`${newName} is already added to phonebook`)

      }
    })
    setPersons(persons.concat(Object))
    setNewName('')
    setNewPhone('')
    //setFNewilterName(filterName.concat(filterName))

  }
  const handleNameChange = (event) => {
    //console.log(event.target.value)
    setNewName(event.target.value)
  }
  const handlePhoneChange = (event) => {
    setNewPhone(event.target.value)
  }
  // const handleFilterName = (event) => {
  //   setFNewilterName(event.target.value)
  // }

  // let result = persons.filter(persons => persons.name === filterName)
  // console.log(result)
  /*let isinclude = persons.includes(newName)
  console.log(isinclude)
  if (isinclude === true ) window.alert(`${newName} is already added to phonebook`)*/

  return (
    <div>
      {/* <div>
      <h2>phonebook</h2>
      <form onSubmit = {add}>
        <div>
          filter shown with
          <input value={filterName}
            onChange={handleFilterName}
          />
        </div>
      </form>
    </div> */}
      <h2>Add a new </h2>

      <form onSubmit={add}>
        <div>
          name:
          <input value={newName}
            onChange={handleNameChange}
          />
        </div>
        <div>
          phone:
          <input value={newPhone}
            onChange={handlePhoneChange} />
        </div>

        <h2>Numbers</h2>


        <div>
          <button type="submit">add</button>
        </div>
      </form>
      <form>

        {persons && persons.map(note =>
          <p key={note.id}>  {note.name} {note.phone}</p>
        )}
        {/* {persons && persons.map(note =>
          <li key={note.id}> {note.phone} </li>
        )} */}

      </form>
    </div>

  )
}

export default App
