    event.preventDefault()
    const nameObject = {
      content : newName,
      id : persons.length + 1
    }
    console.log(addName)
    setPersons(persons.concat(nameObject))
    setNewName('')
  }

  const [ newName, setNewName ] = useState('')
  const handleChange = (event) =>{
